import React, { useEffect, useState } from 'react';
import axios from 'axios';
import ReactECharts from 'echarts-for-react';

const TotalYieldByDistrictChart = () => {
  const [chartData, setChartData] = useState({ years: [], dataByDistrict: {} });

  useEffect(() => {
    const fetchData = async () => {
      const config = {
        method: 'get',
        url: 'https://ig.gov-cloud.ai/pi-get-data-quarkus/v1.0/bigQuery/66b205e3b7ea59463e796645/data?size=10000',
        headers: {
          'Authorization': 'Bearer eyJhbGciOiJSUzI1NiIsInR5cCIgOiAiSldUIiwia2lkIiA6ICI3Ny1NUVdFRTNHZE5adGlsWU5IYmpsa2dVSkpaWUJWVmN1UmFZdHl5ejFjIn0..VYZcPeujLulFYl1ZOpdSXhafpRAM6qmWK48le-g30m9DGyfxsHqBSVERxBa1IqK5jTareyXvXWo2nVBCC67x_P6OfqtZRcMTUCaWQ3x_m6soScI6oCZBdEXjLPR8z_40ihhckO1aIdcqmI1ULlhrp_obPs00VdvKnXM8u9qZIhaOvb79-xYGx8CWd6LY2Foe_J_sW8YjRwEMeeVucqWBVdfJVpJ_gyuQgfigfV78ufjaKyIjy-FZ2t-ZGx5iT-ZsIaxK8eUqStljyQzDF5vZl3ZtJrdDgPZPDgU0ntbllSFSDMPt6i2BgvqQWh_ry4IV8mE2pPFYDicF3Rabzz982w'
        }
      };

      try {
        const response = await axios(config);
        const entities = response.data.model.entities;

        const years = [];
        const dataByDistrict = {};

        entities.forEach(item => {
          const { year, total_yield, district } = item.data;

          if (!years.includes(year)) {
            years.push(year);
          }

          if (!dataByDistrict[district]) {
            dataByDistrict[district] = [];
          }

          dataByDistrict[district].push({ year, total_yield });
        });

        setChartData({ years, dataByDistrict });
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  const getOption = () => {
    const series = Object.keys(chartData.dataByDistrict).map(district => ({
      name: district,
      type: 'line',
      data: chartData.dataByDistrict[district].map(item => item.total_yield),
    }));

    return {
      title: {
        text: '',
      },
      tooltip: {
        trigger: 'axis',
        axisPointer: {
          type: 'shadow'
        }
      },
      legend: {
        data: Object.keys(chartData.dataByDistrict),
      },
      xAxis: {
        type: 'category',
        data: chartData.years,
      },
      yAxis: {
        type: 'value',
      },
      series,
    };
  };

  return (
    <ReactECharts option={getOption()} style={{ height: 400, width: '100%' }} />
  );
};

export default TotalYieldByDistrictChart;
